import React, { useEffect } from 'react';
import { useLocation, NavLink} from 'react-router-dom';

// Importamos los estilos
import '../styles/navbar.css';

export default function Navbar(){

    const UbicacionAct = useLocation(); //Ruta actual en la que se está usando el navbar

    const getInicioPath = () => {
      const { pathname } = location;

      // Verifica si la ubicación actual comienza con '/everyone'
      if (pathname.startsWith('/everyone')) {
        return '/everyone'; // Si es así, regresa '/everyone'
      }

      // Verifica si la ubicación actual comienza con '/parents'
      if (pathname.startsWith('/parents')) {
        return '/parents'; // Si es así, regresa '/parents'
      }

      // Si no coincide con ninguna condición, regresa '/'
      return '/';
    };

    /* Como esto tiene que ser responsivo, vamos a usar Bootstrap */

    return (
      <nav className="navbar navbar-expand-lg custom-navbar">
        <div className="container-fluid">
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <NavLink className="nav-link" exact to="/" activeClassName="active">Inicio</NavLink>
              </li>
              <li className="nav-item">
                <NavLink className="nav-link" to="/nuevo-alumno" activeClassName="active">Nuevo Alumno</NavLink>
              </li>
              <li className="nav-item">
                <NavLink className="nav-link" to="/conocenos" activeClassName="active">Conócenos</NavLink>
              </li>
              <li className="nav-item">
                <NavLink className="nav-link" to="/creditos" activeClassName="active">Créditos</NavLink>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    );



}
